import { Component } from 'react';

import './card-container.styles.css';

class CardContainer extends Component {
  render() {
    const {
      name: individualName,
      email: individualEmail,
      id: individualId,
    } = this.props.monster;

    return (
      <div
        className='card-container'
        key={individualId}
      >
        <img
          alt={`monster ${individualName}`}
          src={`https://robohash.org/${individualId}?set=set2&size=180x180`}
        />
        <h2>{individualName}</h2>
        <p>{individualEmail}</p>
      </div>
    );
  }
}

export default CardContainer;
